INSERT INTO post (created_at, updated_at, author_id, id, space_id, text_content) VALUES ('2025-01-05 05:04:25.062985', NULL, 'd5029436-b643-4e7a-aac3-7123562c7811', '286daa30-3875-4f37-b865-6207758dca4f', 'd5029436-b643-4e7a-aac3-7123562c7811', 'Nothing beats the thrill of analyzing a football match, whether it''s dissecting the strategies of professional teams or enjoying a friendly kickabout with mates. The beauty of the game lies in its universal language, connecting people from all walks of life. Every pass, every goal, and every tactical decision tells a story, making each match a unique experience.');
INSERT INTO post (created_at, updated_at, author_id, id, space_id, text_content) VALUES ('2025-01-05 05:04:55.619308', NULL, 'a69a7f50-674d-456a-937b-03f5a7a5f65a', '403ade9c-e8a6-4482-8aab-a289e7cb6448', 'a69a7f50-674d-456a-937b-03f5a7a5f65a', 'Delving into the rich tapestry of cultural anthropology has been an enlightening journey. Studying and documenting cultural practices and traditions offers a unique lens through which we can understand the diverse ways humans interact with their environment and each other. Whether it''s exploring the intricate rituals of a small village or the urban traditions of a bustling city, every discovery adds a new layer to our collective narrative. It''s not just about observing; it''s about becoming a part of the story, respectfully and mindfully.');
INSERT INTO post (created_at, updated_at, author_id, id, space_id, text_content) VALUES ('2025-01-05 05:05:21.286134', NULL, '5eb8865c-2c45-4b97-8098-f79e35b32324', '83f4bfb2-2276-4f66-be42-d41452e95d5c', '5eb8865c-2c45-4b97-8098-f79e35b32324', 'Thrilled to share that our team has successfully initiated a clean water project in the rural communities of Cusco. Seeing the smiles on the faces of the local children as they turned on their new water tap was priceless. This is just the beginning; we''re committed to driving more change!');
