INSERT INTO post (created_at, updated_at, author_id, id, space_id, text_content) VALUES ('2025-01-05 05:48:38.431668', NULL, '4c69120c-2479-4ac4-a992-d343b7e81750', 'd3b92bed-3e50-49a3-b3cd-bd8e1de8f2ed', '4c69120c-2479-4ac4-a992-d343b7e81750', 'Transitioning to sustainable energy is not just an option, but a necessity for our future. By developing and promoting renewable energy solutions like solar, wind, and hydroelectric power, we can significantly reduce our carbon footprint and combat climate change. Let''s invest in a greener tomorrow and ensure a healthier planet for future generations.');
